import React, { useState } from 'react';
import './ExpenseForm.css'
const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDate, setEnteredDate] = useState('')
    const [addExpenseBoolean, setAddExpenseBoolean] = useState(false)
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: ''
    // })
    const titleChangeHandler = (event) => {
        // setUserInput({
        //     ...userInput,
        //     enteredTitle: event.target.value
        // })
        // setUserInput((prevState)=>{
        //     return {...prevState,enteredTitle:event.target.value}
        // })
        setEnteredTitle(event.target.value)
    }
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value)
        // setUserInput({
        //     ...userInput,
        //     enteredAmount: event.target.value
        // })
    }
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value)
        // setUserInput({
        //     ...userInput,
        //     enteredDate: event.target.value
        // })
    }
    const submitHandler = (e) => {
        e.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate),
        }
        props.onSaveExpenseData(expenseData)
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('')
        // console.log(expenseData)
    }
    const cancelBtnHandler = () => {
        setAddExpenseBoolean()
    }
    return (
        <>
            {!addExpenseBoolean && <div className="new-expense__controls">
                <div className='new-expense__actions'>
                    <button onClick={() => setAddExpenseBoolean(true)} type='submit'>Add Expense</button>
                </div>
            </div>}
            {addExpenseBoolean && <form onSubmit={submitHandler}>
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                        <label htmlFor="">Title</label>
                        <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
                    </div>
                    <div className="new-expense__control">
                        <label htmlFor="">Amount</label>
                        <input type="number" value={enteredAmount} onChange={amountChangeHandler} min='0.01' step='0.01' />
                    </div>
                    <div className="new-expense__control">
                        <label htmlFor="">Date</label>
                        <input type="date" value={enteredDate} onChange={dateChangeHandler} min='2019-01-01' max='2022-12-31' />
                    </div>
                </div>
                <div className='new-expense__actions'>
                    <button onClick={()=>cancelBtnHandler}>Cancel</button>
                    <button type='submit'>Add Expense</button>
                </div>
            </form>
            }
        </>
    )
}
export default ExpenseForm
