import React, { useState, useEffect,useReducer ,useContext } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';
import AuthContext from '../../store/auth-context';
import Input from '../UI/Input/Input';
const emailReducer=(state,action)=>{
  if(action.type==='USER_INPUT'){
   return {
    value:action.val,
    isValid:action.val.includes('@')
   }
  }
  if(action.type==='INPUT_BLUR'){
    return{
      value:state.value,
      isValid:state.value.includes('@')
    }
  }
  return {
    value:'',
    isValid:false
  }
}
const passwordReducer=(state,action)=>{

  if(action.type==="PASS_INPUT"){
    return{
      value:action.val,
      isValid:action.val.trim().length > 6
    }
  }
  if(action.type==="PASS_BLUR"){
    return{
      value:state.value,
      isValid:state.value.trim().length > 6
    }
  }
  return{
    value:'',
    isValid:false
  }
}
const Login = (props) => {
  const authCtx = useContext(AuthContext)
  // const [enteredEmail, setEnteredEmail] = useState('');
  // const [emailIsValid, setEmailIsValid] = useState();
  // const [enteredPassword, setEnteredPassword] = useState('');
  // const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const[emailState,dispatchEmail]=useReducer(emailReducer,{value:'',isValid:null})
  const [passwordState,dispatchPassword]=useReducer(passwordReducer,{value:'',isValid:null})

  useEffect(() => {
    console.log('EFFECT RUNNING');

    return () => {
      console.log('EFFECT CLEANUP');
    };
  }, []);

  const {isValid : emailIsValid}=emailState;
  const {isValid : passwordIsValid}=passwordState;

  useEffect(() => {
    const identifier = setTimeout(() => {
      console.log('Checking form validity!');
      setFormIsValid(
        emailIsValid && passwordIsValid
      );
    }, 500);

    return () => {
      console.log('CLEANUP');
      clearTimeout(identifier);
    };
  }, [emailIsValid, passwordIsValid]);

  const emailChangeHandler = (event) => {
    // setEnteredEmail(event.target.value);
    dispatchEmail({type:'USER_INPUT',val:event.target.value})
    setFormIsValid(
      // emailState.isValid && enteredPassword.trim().length > 6
      emailState.isValid && passwordState.isValid
    );
  };

  const passwordChangeHandler = (event) => {
    // setEnteredPassword(event.target.value);
    dispatchPassword({type:"PASS_INPUT",val:event.target.value})

    setFormIsValid(
      emailState.isValid && passwordState.isValid
    );
  };

  const validateEmailHandler = () => {
    // setEmailIsValid(emailState.isValid);
    dispatchEmail({type:'INPUT_BLUR'})
  };

  const validatePasswordHandler = () => {
    // setPasswordIsValid(enteredPassword.trim().length > 6);
    dispatchPassword({type:"PASS_BLUR"})
  };

  const submitHandler = (event) => {
    event.preventDefault();
    authCtx.onLogin(emailState.value, passwordState.value);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <Input id='email' type='email' label='E-Mail' isValid={emailIsValid} value={emailState.value} onChange={emailChangeHandler} onBlur={validateEmailHandler} />

        <Input id='password' type='password' label='password' isValid={passwordIsValid} value={passwordState.value} onChange={passwordChangeHandler} onBlur={validatePasswordHandler} />
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
